css:
.header {
background: #000000;
}

.footer {
background: #000000;
color: #fff !important;
}

@media(max-width: 480px) {
#search-bar {
display: block !important;
}

.product__buy button {
color: #ffffff;
background-color: #e60000;
border-color: #e60000;
width: 100%;
}

.product__buy button:hover {
background: #e60000;
border-color: #e60000;
}

.address-form button, .cart__btn-checkout, .checkout__account button, .login__email-btn, .minicart__btn-checkout, .minicart__empty a, .payment__method button, .product-card__buy button, .product__buy button, .product__sticky-buy a {
background-color: #e60000!important;
border-color: #e60000!important;
}

.checkout__col .payment .list-group {
display: flex;
}

@media(max-width: 575.98px) {
.info-bar__nav {
flex-wrap: wrap;
justify-content: space-around
}

.info-bar__nav .col-auto {
max-width: 50%;
padding-left: 0;
padding-right: 0;
}

.info-bar__title, .info-bar__text {
font-size: 12px !important;
}

.header__submenu {
background: #000000 !important;
}

.header__submenu {
color: #fff !important
}

@media(max-width: 767px) {
#product-description iframe {
width: 100% !important;
height: auto !important;
}

.payment__gateway[data-label="Pix"] {
  order: 1;
  border-top: 1px solid
}

.payment__gateway[data-label="Cartão de crédito"] {
  order: 2
} 

.payment__gateway[data-label="Boleto"] {
  order: 3
}

.product-card .prices__value {
  display: none
}
.product-card .prices span {
  display: flex;
  flex-direction: column
}
.product-card .prices__discount {
  order: -1;
  font-size: var(--font-size-lg)
}
.product-card .prices__installments {
  display: flex;
}
.product-card .prices__installments:before {
  content: 'ou \00a0'
}
.product-card .prices__installments span {
  padding-left: 5px
}
#product-actions .prices__discount span:after {
  content: '(10%OFF)'
}
